package gui.popup;import document.Document;import document.project.ProjectEnvironment;import document.project.ProjectRepository;import javafx.event.ActionEvent;import javafx.event.EventHandler;import javafx.scene.control.Button;import javafx.scene.layout.GridPane;import settings.Translator;public class WelcomePopup extends CustomPopup{	private GridPane pane;	private Button newBookPane;	private Button loadBookPane;	public WelcomePopup() {		initGui();		initEvent();	}	private void initEvent() {		newBookPane.setOnAction(new EventHandler<ActionEvent>() {			@Override			public void handle(ActionEvent arg0) {				ProjectRepository.getActiveProjectEnvironment().associateWithNewDocument(new Document());				hide();			}		});		loadBookPane.setOnAction(new EventHandler<ActionEvent>() {			@Override			public void handle(ActionEvent arg0) {				ProjectRepository.getActiveProjectEnvironment().loadButtonPressed();				hide();			}		});	}	private void initGui() {		newBookPane = new Button(Translator.get("New Book"));		loadBookPane = new Button(Translator.get("Load Book"));		pane = new GridPane();		pane.add(newBookPane, 0, 0);		pane.add(loadBookPane, 1, 0);		this.getChildren().add(pane);	}	@Override	public boolean isDisposedWhenClickedOutside() {		return false;	}}